import React, { useState } from 'react';
import '/Users/taopuchong/Desktop/seniorproj/src';

const PortfolioPage = () => {
  const [tabSelected, setTabSelected] = useState('portfolio');

  const handleTabClick = (tab) => {
    setTabSelected(tab);
  };

  const renderTableHeader = () => {
    return (
      <tr>
        <th>Stock</th>
        <th>Amount</th>
        <th>Purchased Price</th>
        <th>Market Price</th>
        <th>Profit/Loss</th>
      </tr>
    );
  };

  const renderTableData = () => {
    const stock = 'AOT';
    const amount = 100;
    const purchasedPrice = 66.5;
    const marketPrice = 70;
    const profitLoss = ((marketPrice - purchasedPrice) / purchasedPrice) * 100;
    const isProfit = profitLoss >= 0;

    return (
      <tr>
        <td>{stock}</td>
        <td>{amount}</td>
        <td>{purchasedPrice.toFixed(2)}</td>
        <td>{marketPrice.toFixed(2)}</td>
        <td className={isProfit ? 'profit' : 'loss'}>
          {profitLoss.toFixed(2)}%
        </td>
      </tr>
    );
  };

  const renderPortfolioTable = () => {
    return (
      <table>
        <thead>{renderTableHeader()}</thead>
        <tbody>{renderTableData()}</tbody>
      </table>
    );
  };

  const renderOrdersTable = () => {
    const stock = 'AOT';
    const amount = 100;
    const orderedPrice = 66.5;
    const strategy = 'Engulfing';
    const executedTime = '14/03/2023 16:20';

    return (
      <table>
        <thead>
          <tr>
            <th>Stock</th>
            <th>Amount</th>
            <th>Ordered Price</th>
            <th>Strategy</th>
            <th>Executed Time</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>{stock}</td>
            <td>{amount}</td>
            <td>{orderedPrice.toFixed(2)}</td>
            <td>{strategy}</td>
            <td>{executedTime}</td>
          </tr>
        </tbody>
      </table>
    );
  };

  return (
    <div className="portfolio-page">
      <div className="tabs">
        <div
          className={tabSelected === 'portfolio' ? 'tab selected' : 'tab'}
          onClick={() => handleTabClick('portfolio')}
        >
          Portfolio
        </div>
        <div
          className={tabSelected === 'orders' ? 'tab selected' : 'tab'}
          onClick={() => handleTabClick('orders')}
        >
          Orders
        </div>
      </div>
      <div className="content">
        {tabSelected === 'portfolio' && renderPortfolioTable()}
        {tabSelected === 'orders' && renderOrdersTable()}
      </div>
    </div>
  );
};

export default PortfolioPage;
