import React, { useState } from 'react';
import '/Users/taopuchong/Desktop/seniorproj/src/StrategyManagement.css';

const StrategyManagementPage = () => {
  const [strategies, setStrategies] = useState([
    {
      name: 'Engulfing',
      lastSaved: '12/03/2023 18:20',
      lastExecuted: '14/03/2023 16:20',
      isActive: false,
    },
  ]);

  const handleToggleActivation = (strategyName) => {
    setStrategies((prevStrategies) =>
      prevStrategies.map((strategy) =>
        strategy.name === strategyName
          ? { ...strategy, isActive: !strategy.isActive }
          : strategy
      )
    );
  };

  return (
    <div className="strategy-management-page">
      <div className="header">
        <div className="back-btn">Back</div>
        <div className="active-tab">Strategy Management</div>
      </div>
      <table className="table">
        <thead>
          <tr>
            <th>Strategy</th>
            <th>Last Saved</th>
            <th>Last Executed</th>
            <th>Activation</th>
          </tr>
        </thead>
        <tbody>
          {strategies.map((strategy) => (
            <tr key={strategy.name}>
              <td>{strategy.name}</td>
              <td>{strategy.lastSaved}</td>
              <td>{strategy.lastExecuted}</td>
              <td>
                <div
                  className={`toggle-switch ${
                    strategy.isActive ? 'active' : ''
                  }`}
                  onClick={() => handleToggleActivation(strategy.name)}
                >
                  <div className="toggle-switch-handle"></div>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StrategyManagementPage;
