import React, { useState } from "react";
import { Controlled as CodeMirror } from "react-codemirror2";
import "codemirror/lib/codemirror.css";
import "codemirror/theme/material.css";
import "codemirror/mode/javascript/javascript";
import "./CodeEditorPage.css";


function CodeEditorPage() {
  const [theme, setTheme] = useState("light");
  const [code, setCode] = useState("// Type your code here...");
  const [activeTab, setActiveTab] = useState("file-explorer");

  const handleRunCode = () => {
    // Implement code execution logic here
  };

  const handleSaveCode = () => {
    // Implement code saving logic here
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case "file-explorer":
        return (
          <div className="file-explorer">
            {/* Implement file explorer component here */}
          </div>
        );
      case "search":
        return (
          <div className="search">
            {/* Implement search component here */}
          </div>
        );
      case "extension":
        return (
          <div className="extension">
            {/* Implement extension component here */}
          </div>
        );
      case "settings":
        return (
          <div className="settings">
            <label>
              Theme:
              <select
                value={theme}
                onChange={(e) => setTheme(e.target.value)}
              >
                <option value="light">Light</option>
                <option value="dark">Dark</option>
              </select>
            </label>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className={`CodeEditorPage ${theme === "light" ? "light-theme" : "dark-theme"}`}>
      <div className="left-panel">
        <div className="tabs">
          <button onClick={() => setActiveTab("file-explorer")}>
            <img src='file.png' alt="File Explorer" />
          </button>
          <button onClick={() => setActiveTab("search")}>
            <img src='search.png' alt="Search" />
          </button>
          <button onClick={() => setActiveTab("extension")}>
            <img src='extension.png' alt="Extensions" />
          </button>
          <button onClick={() => setActiveTab("settings")}>
            <img src='setting.png' alt="Settings" />
          </button>
        </div>
        <div className="tab-content">
          {renderTabContent()}
        </div>
      </div>
      <div className="code-editor">
        <div className="toolbar">
          <button onClick={handleRunCode}>
            <img src={runIcon} alt="Run" />
          </button>
          <button onClick={handleSaveCode}>
            <img src={saveIcon} alt="Save" />
          </button>
        </div>
        <CodeMirror
          value={code}
          options={{
            mode: "javascript",
            theme: theme === "light" ? "default" : "material",
            lineNumbers: true,
          }}
          onBeforeChange={(editor, data, value) => {
            setCode(value);
          }}
        />
      </div>
    </div>
  );
}

export default CodeEditorPage;
