import React, { useState } from "react";
import "./CodeEditorPage.css";
import runIcon from "./run-icon.svg"; // Replace with the path to your run icon
import saveIcon from "./save-icon.svg"; // Replace with the path to your save icon

function CodeEditorPage() {
  const [theme, setTheme] = useState("light");
  const [code, setCode] = useState("// Type your code here...");

  const handleRunCode = () => {
    // Implement code execution logic here
  };

  const handleSaveCode = () => {
    // Implement code saving logic here
  };

  return (
    <div className={`CodeEditorPage ${theme === "light" ? "light-theme" : "dark-theme"}`}>
      <div className="left-panel">
        <div className="file-explorer">
          {/* Implement file explorer component here */}
        </div>
        <div className="search">
          {/* Implement search component here */}
        </div>
        <div className="extension">
          {/* Implement extension component here */}
        </div>
        <div className="settings">
          <label>
            Theme:
            <select
              value={theme}
              onChange={(e) => setTheme(e.target.value)}
            >
              <option value="light">Light</option>
              <option value="dark">Dark</option>
            </select>
          </label>
        </div>
      </div>
      <div className="code-editor">
        <div className="toolbar">
          <button onClick={handleRunCode}>
            <img src={runIcon} alt="Run" />
          </button>
          <button onClick={handleSaveCode}>
            <img src={saveIcon} alt="Save" />
          </button>
        </div>
        <textarea
          className="editor"
          value={code}
          onChange={(e) => setCode(e.target.value)}
          wrap="off"
          spellCheck="false"
        />
      </div>
    </div>
  );
}

export default CodeEditorPage;
